INSERT INTO tourist_spots (external_id, name, address, latitude, longitude, description, category) VALUES
                                                                                                       ('SPOT001', '성산일출봉', '제주특별자치도 서귀포시 성산읍 성산리', 33.4584, 126.9426, '유네스코 세계자연유산으로 지정된 제주의 대표 관광지', '자연경관'),
                                                                                                       ('SPOT002', '한라산 국립공원', '제주특별자치도 제주시 해안동', 33.3616, 126.5292, '한국 최고봉 한라산과 아름다운 자연을 만날 수 있는 국립공원', '자연경관'),
                                                                                                       ('SPOT003', '정방폭포', '제주특별자치도 서귀포시 동홍동', 33.2368, 126.5065, '바다로 직접 떨어지는 국내 유일의 해안폭포', '자연경관'),
                                                                                                       ('SPOT004', '우도', '제주특별자치도 제주시 우도면', 33.4968, 126.9503, '제주 동쪽 끝에 위치한 아름다운 섬', '섬'),
                                                                                                       ('SPOT005', '만장굴', '제주특별자치도 제주시 구좌읍 김녕리', 33.5266, 126.7712, '세계자연유산으로 지정된 용암동굴', '동굴'),
                                                                                                       ('SPOT006', '천지연폭포', '제주특별자치도 서귀포시 천지동', 33.2447, 126.5548, '천지가 연결된다는 의미의 아름다운 폭포', '자연경관'),
                                                                                                       ('SPOT007', '중문 대포해안', '제주특별자치도 서귀포시 중문동', 33.2384, 126.4137, '검은 현무암과 에메랄드빛 바다의 조화', '해안'),
